import logo from './logo.svg';
import './App.css';
import { useCallback, useRef, useState } from 'react';
import { produce } from 'immer';

const numRows = 10;
const numCols = 10;
function App() {

  //initializing grid
  const [grid, setGrid] = useState(() => {
    const rows = [];
    for (let i = 0; i < numRows; i++) {
      rows.push(Array.from(Array(numCols), () => 0));
    }
    return rows;
  });
  console.log(grid);

  //runnging state check
  const [running, setRunning] = useState(false);
  const runningRef = useRef(running);
  runningRef.current = running;

  //using callback function for generating the function only once but the state of the grid will change and as will change the grid accordingly
  const runSimulation = useCallback(()=>{
    if(!runningRef) return;
    //simulate 
    setGrid(g=>{
      return produce(g,gridCopy => {
        for(let i=0;i<numRows;i++){
          for(let j=0;j<numCols;j++){
            let neighbours = 0;

          }
        }
      })
    });
    setTimeout(runSimulation,1000);
  },[]);
  return (
    <>
      <button onClick={()=>{
        setRunning(!running);
      }}>
        {running ? "Stop" : "Start"}
      </button>
      <div style={{
        display: 'grid',
        gridTemplateColumns: `repeat(${numCols},20px)`
      }}>
        {grid.map((rows, i) =>
          rows.map((cols, j) => (
            <div
              onClick={() => {
                const newGrid = produce(grid, gridCopy => {
                  gridCopy[i][j] = grid[i][j] ? 0 : 1;
                });
                setGrid(newGrid);
              }}
              key={`${i}-${j}`}
              style={{
                width: 20,
                height: 20,
                backgroundColor: grid[i][j] ? "orange" : undefined,
                border: "solid 1px black",
                borderRadius: '2px'
              }}
            />
          ))
        )}
      </div>
    </>
  );
}

export default App;
