import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const numRows = 50;
const numCols = 50;
function App() {
  const [grid,setGrid] = useState(()=>{
    const rows = [];
    for(let i=0;i<numRows;i++){
      rows.push(Array.from(Array(numCols),()=>0));
    }
    return rows;
  });
  console.log(grid);
  return (
    <div style={{
      display:'grid',
      gridTemplateColumns:`repeat(${numCols},20px)`
    }}>
      {grid.map((rows,i) => 
        rows.map((cols,j)=>(
          <div
          key={`${i}-${j}`}
          style={{
            width:20,
            height:20,
            backgroundColor: grid[i][j]?"green":undefined,
            border:"solid 1px black",
            borderRadius:'2px',
            paddingLeft:'1px',
            paddingRight:'1px',
            paddingTop:'1px'
          }}
          />
        ))
      )}
    </div>
  );
}

export default App;
